let cart=JSON.parse(localStorage.getItem("cart"))||[];const cartContainer=document.getElementById("cart-container");const totalContainer=document.getElementById("total");function displayCart(){cartContainer.innerHTML="";if(cart.length===0){cartContainer.innerHTML="<p>Tu carrito está vacío</p>";totalContainer.innerHTML="$0";return}let total=0;cart.forEach(((product,index)=>{const productElement=document.createElement("div");productElement.classList.add("cart-item");productElement.innerHTML=`\n            <h3>${product.name}</h3>\n            <p>Unidad: $${product.price.toFixed(2)}</p>\n            <div>\n                <button onclick="changeQuantity(${index}, -1)">-</button>\n                <span>Cantidad: ${product.quantity}</span>\n                <button onclick="changeQuantity(${index}, 1)">+</button>\n            </div>\n            <p>Total: $${(product.price*product.quantity).toFixed(2)}</p>\n            <button onclick="removeFromCart(${index})">Eliminar</button>\n        `;cartContainer.appendChild(productElement);total+=product.price*product.quantity}));totalContainer.innerHTML=`$${total.toFixed(2)}`}function changeQuantity(index,change){if(cart[index].quantity+change>0){cart[index].quantity+=change}localStorage.setItem("cart",JSON.stringify(cart));displayCart()}function removeFromCart(index){cart.splice(index,1);localStorage.setItem("cart",JSON.stringify(cart));displayCart()}document.addEventListener("DOMContentLoaded",displayCart);